CREATE CACHED TABLE messages(id INT GENERATED BY DEFAULT AS IDENTITY (START WITH 1, INCREMENT BY 1) NOT NULL, class VARCHAR(128), messageName VARCHAR_IGNORECASE(128), typeId INT, messageId INT, byteSize INT, phase VARCHAR(2), rate VARCHAR(8), scheduled VARCHAR(5), providerId INT, PRIMARY KEY (id));
CREATE CACHED TABLE types(typeId INT GENERATED BY DEFAULT AS IDENTITY (START WITH 1, INCREMENT BY 1) NOT NULL, messageType VARCHAR(512), PRIMARY KEY (typeId));
CREATE CACHED TABLE elements(id INT, elementName VARCHAR_IGNORECASE(256), providerId INT);
CREATE CACHED TABLE sources(messageId INT, suId INT, providerId INT);
CREATE CACHED TABLE destinations(messageId INT, suId INT, providerId INT);
CREATE CACHED TABLE sus(suId INT GENERATED BY DEFAULT AS IDENTITY (START WITH 1, INCREMENT BY 1) NOT NULL, suName VARCHAR(64), PRIMARY KEY(suId));
